
// Response wrapper
module.exports.resSuccess = function (req, res, data) {
	res.status(200)
	res.send({
		isExtensionResponse : true,
		data                : data
	})
}

module.exports.resError = function(req, res, error){
	res.status(422)
	// construct the error message
	res.send({
		isExtensionError : true,
		errors           : bridgeErrorFormat(error)
	})
}

function bridgeErrorFormat(error){
	return Object.keys(error)
	.map(function(errorKey){
		return {
			errorKey : error[errorKey],
			path     : errorKey
		}
	})
}