/**
 * Attaches all middleware 
 */
exports.responseTime    = require('./middlewares/response-time')
exports.reqParams       = require('./middlewares/req-params')
exports.responseTimeout = require('./middlewares/response-timeout')
exports.pragmaticRest   = require('./middlewares/pragmatic-rest')
exports.wrapInBuiltApp  = require('./middlewares/wrap-in-built-app')
exports.wrapInBuilt     = require('./middlewares/wrap-in-built')
exports.requestLogger   = require('./middlewares/request-logger')
exports.consoleLogger   = require('./middlewares/console-logger')

exports.load = function (routePath, middlewares, app, extApp) {
	middlewares.forEach(function(middleware){
		app.use(routePath, middleware.bind(extApp))
	})
}